mapper.delete:
delete
from sns_prj.`like`
where user_id = #{user_id}
and board_id = #{board_id}

mapper.select:

	select
		user_id
	from
		sns_prj.`like`
	where user_id = #{user_id}
	and board_id = #{board_id}

mapper.update:
update sns_prj.`common_board`
set like_cnt = (select count(*) from `like` where board_id=#{board_id})
where board_id = #{board_id}

mapper.select:
select like_cnt
from sns_prj.`common_board`
where board_id = #{board_id}
