mapper.select:
		select
		user_id , u_password , user_name, email, tel, thumb_nail, user_type
		from
		sns_prj.`user`
		where user_id = #{user_id}
		and u_password = #{u_password}

mapper.update:
 
	update sns_prj.`user`
	set u_password = #{u_password} , email = #{email}, tel = #{tel} , user_name = #{user_name}
	where user_id = #{user_id}

mapper.select:
select thumb_nail
from sns_prj.`user`
where user_id = #{user_id}
